-- ----------------------------
-- Records of teachers
-- ----------------------------
INSERT INTO `teachers` VALUES (1, '肖秀荣', '肖秀荣，福建人，中央民族大学教授、研究生导师，长期从事马克思主义基本理论的研究与教学工作。1967年毕业于北京大学国际政治系（现北大国际关系学院），1991年至2004年为教育部考试中心全国硕士研究生政治理论入学统一考试命题组成员，曾任政治命题组副组长及学科组组长，参与历年考研政治大纲的修订和《政治理论复习参考书》（“红宝书”）的编写。14年的考研政治命题经验无疑使肖秀荣教授成为最具影响力的命题专家。', 'http://127.0.0.1:3000/xiaoxiurong.jpg', 'https://pan.baidu.com/s/1bqmZLF6Ewf_tM-M3pxmLGQ', '提取码：1234');
INSERT INTO `teachers` VALUES (8, '陆寓丰', '陆寓丰，南京大学马克思主义理论专业博士生，曾是江苏考研政治阅卷员，被赞是身材学历双高，曾自创了独特火辣教学法，专门整治那些不听课的学生，因为腿长她被学生们称为“腿姐”。', 'http://127.0.0.1:3000/1649953054191_陆寓丰.png', 'https://pan.baidu.com/s/1bqmZLF6Ewf_tM-M3pxmLGQ ', '提取码：1234');
INSERT INTO `teachers` VALUES (16, '徐涛', '考研政治徐涛，教育类自媒体，由徐涛运营，微博认证信息为“微博教育账号”。', 'http://127.0.0.1:3000/1649956234594_徐涛.jpg', 'https://pan.baidu.com/s/1bqmZLF6Ewf_tM-M3pxmLGQ ', '提取码：1234');

